import debug from 'debug';

type KLogger = {
    /** Informational logs */
    info: debug.Debugger;
    /** Warning logs */
    warn: debug.Debugger;
    /** Error logs */
    error: debug.Debugger;
};
/**
 * App Logger
 * @private
 */
declare class Logger implements KLogger {
    readonly info: debug.Debugger;
    readonly warn: debug.Debugger;
    readonly error: debug.Debugger;
    private readonly _prefix;
    /**
     * App Logger
     * @param name the kapp name
     */
    constructor(name: string);
}

export { KLogger, Logger };
