/** A Kustomer response */
type Response<T = unknown> = {
    /** The data of the response */
    readonly data: ResponseData<T>;
};
/** A Kustomer array response */
type ArrayResponse<T = unknown> = {
    /** The data of the response */
    readonly data: ResponseData<T>[];
};
/** A Kustomer response data */
type ResponseData<T = unknown> = {
    /** The type of the response data */
    readonly type: string;
    /** The id of the response data */
    readonly id: string;
    /** The attributes of the response data */
    readonly attributes: T;
};

export { ArrayResponse, Response, ResponseData };
