type Hook = {
    /** The description of the hook */
    description: string;
    /** The name of the hook */
    eventName: string;
    /** The hash of the hook */
    hash: string;
    /** The URL of the hook */
    url: string;
    /** When the hook was created */
    createdAt: string;
    /** When the hook was updated */
    updatedAt: string;
    /** When the hook was last modified */
    modifiedAt: string;
};

export { Hook };
