/** The supported type of the given setting */
type SettingType = 'string' | 'number' | 'boolean' | 'secret';
type SettingManifest = {
    /** The description of the given setting */
    description?: string;
    /** The default value of the given setting */
    defaultValue?: string;
    /** The supported type of the given setting */
    type: SettingType;
    /** Specifies a setting that a user is expected to update after app install for a functional app */
    required?: boolean;
    /** If true, this hides the setting from being shown in the default setting page*/
    hidden?: boolean;
};
type Setting<T = unknown> = SettingManifest & {
    /** The name of the given setting */
    name: string;
    /** The value of the given setting */
    value?: T;
};
type SettingsManifest = {
    /** The category of a group of settings */
    [category: string]: {
        /** The name of the given setting */
        [name: string]: SettingManifest;
    };
};

export { Setting, SettingManifest, SettingType, SettingsManifest };
